import React from "react";
import './Homepage.css';
import { Button } from "@material-ui/core";
import {Link} from "react-router-dom";
function Homepage() {
    return(
        <div className = "Homepage__text" >
            <div>
                <h1 className = "text__subheadings"><span style = {{fontSize: "50px"}}>Juego</span></h1>
                <h1>Juego's Goal</h1>
                <p>Juego is an online platform that allows user to easily and safely find new people to game with on the most popular games.
                    The platforms intuitive UI allows users to quickly and easily filter out other users based on metrics like skill, game preference, and gaming platform.
                    <br></br><br></br>
                    Traditional platforms like Xbox live and the PlayStation Network and their built in 'friend request' features present a risk to many users. In the past users have been targeted by mallicious actors using denial of service attacks (DOS) upon the acceptence of a friend request on these platforms
                    Security flaws like this leads to an under utilizations of a platforms networking capabilities and has lead to a decline in the quality of the online gaming experience
                    By putting a face to the gamertag, Juego hopes to mitigate this risk by implementing a number a features to ensure that the person swiping right on you is not going to make you lose your service
                </p>

                <h1 className = "text__subheadings"><span style = {{fontSize: "50px"}}>Sign Up for FREE today and Start having a real gaming experience!</span></h1>
            </div>


            <div className = "signup">    
                <Button className = "lbutton" variant="outlined" size= "large" component = {Link} to = "/login">
                    Sign Up
                </Button>
            </div>
        </div>
    )
}

export default Homepage;
