import React from "react";
import { Divider,Box } from "@material-ui/core";
import {Link} from "react-router-dom";
import ArrowBackIos from "@material-ui/icons/ArrowBackIos";
import { IconButton } from "@material-ui/core";
import { Button } from "@material-ui/core";
import "./Profilepage.css";
import { database, UUID, uservar} from "./firebase";

class Profilepage extends React.Component {


    state = {
        profile_test11: null
    }

    componentDidMount() {
        console.log('mounted')
        database.collection('USERS').where('__name__', '==' , UUID).get().then(snapshot => {
            const profile_test11 = []
            snapshot.forEach(doc => {
                const data = doc.data()
                profile_test11.push(data)
            })
            this.setState({ profile_test11: profile_test11 })
            //console.log(snapshot)
        }).catch(error => console.log(error))
    }

    render() {
        return (
            <div className="ViewProfile">
                <Box className="heading">

                <IconButton className = "button__edit" component = {Link} to = "/swipepagemain">
                    <ArrowBackIos fontSize = "large" />
                </IconButton>

                <h1>Profile</h1>

                <Button className = "button__edit" variant="outlined" size="large" component = {Link} to = "/editprofile">
                        Edit profile
                </Button>

                </Box>
                <Divider className="divider" variant= "middle"/>

                <Box classname="heading">
                <h1 class="profileHeader">Your Profile</h1>
                <img src={uservar} id="profileImage" class="profileIMG"/>
                <div class="break"></div>

                {
                    this.state.profile_test11 &&
                    this.state.profile_test11.map( profile => {
                        return(
                            <div class="profileInfo">
                                <p>Name: {profile.name}</p>
                                <p>Bio: {profile.bio}</p>
                                <p>Game1: {profile.game1}</p>
                                <p>Game2: {profile.game2}</p>
                                <p>Game3: {profile.game3}</p>
                                <p>Overwatch: {profile.overwatch}</p>
                                <p>Chess: {profile.chess}</p>
                                <p>ApexLegends: {profile.apexLegends}</p>
                                <p>----------------------</p>
                            </div>
                        )
                    })
                }
                </Box>
            </div>
        )
    }
}

export default Profilepage;
